mapper.select:
SELECT B.board_no ,B.board_title ,B.board_content ,B.board_writer ,DATE_FORMAT(B.reg_date, '%Y-%m-%d') "reg_date" ,DATE_FORMAT(B.mod_date, '%Y-%m-%d') "mod_date" FROM board B JOIN member M ON B.board_writer = M.member_no
B.board_title LIKE CONCAT('%', #{keyword}, '%') OR M.member_id LIKE CONCAT('%', #{keyword}, '%')
LIMIT #{limitPageNo}, #{numPerPage}
